import React, {useState} from 'react'
import Axios from 'axios'



const Forms = () => {
    const [data, setData] = useState({
      review: ''
    })

    function submit(e){
      e.preventDefault()
      Axios.post({
        review: data.review
      })
      .then(res=>{
        res.data
      })
    }

    function handle (e) {
      const newdata={...data}
      newdata[e.target.id] =- e.target.value
      setData(newdata)
    }

    return (
  
    <div className='forms'>
      <form onSubmit={(e)=> submit(e)}>
        <input onChange={(e)=>handle(e)} id='review' value={data.review} placeholder='Leave A Review' type="text" />
        <button>Submit</button>
      </form>

    </div>

    );
  }

export default Forms
